container.literal_block:
// Resolve the import dependencies (if any) and flatten the model.

if(model->hasUnresolvedImports()) {
    auto importer = libcellml::Importer::create();

    // Submit the model to the importer and the absolute location 
    // against which the import reference paths will be resolved.
    importer->resolveImports(model, "");

    printIssues(importer);

    // Print a list of dependencies for the current unflattened model.
    printImportDependencies(model);

    // Retrieve a "flattened" (ie: import-free) model from the importer,
    // and use it to over-write the current model.cd 
    model = importer->flattenModel(model);

    printImportDependencies(model);
}

# Resolve any import dependencies (if present) in the model.
if(model.hasUnresolvedImports()) {
# Create an Importer instance. importer = Importer()
# Submit the model to the importer and the absolute location # against which the import reference paths will be resolved. importer.resolveModelImports(model, "resources/") print_issues_to_terminal(importer)
# Print a list of sources that this model requires. This list will # be empty after the model has been flattened. print_import_dependencies(model)
# Retrieve a "flattened" (ie: import-free) model from the importer, # and use it to over-write the current model. model = importer.flattenModel(model) }
