def module_from_file(input):
# Check the extension is stripped during input. if input[-3:] != '.py': module_file = input + ".py" module_name = input.split("/")[-1] else: module_file = input module_name = ".".join(input.split("/")[-1].split(".")[:-1])
# Import the generated code as a module, and return it. spec = importlib.util.spec_from_file_location(module_name, module_file) module = importlib.util.module_from_spec(spec) sys.modules[module_name] = module spec.loader.exec_module(module) return module
